import React, { useState, useEffect } from "react";
import "./Home.css";

const Home = () => {
  const textArray = [
    "Stay updated with upcoming programming contests.",
    "Explore and participate in contests hosted by top platforms like Codeforces, LeetCode, HackerRank, and CodeChef.",
    "Never miss a coding competition again. Click on a platform in the navigation above to view the upcoming contests.",
  ];
  let iSpeed = 100; // time delay of print out
  let iIndex = 0; // start printing array at this position
  let iArrLength = textArray[0].length; // the length of the text array
  let iScrollAt = 20; // start scrolling up at this many lines
  let iTextPos = 0; // initialise text position
  let sContents = ""; // initialise contents variable
  let iRow; // initialise current row

  useEffect(() => {
    const destination = document.getElementById("typedtext");
    if (destination) {
      typewriter(destination);
    }
  }, []);

  const typewriter = (destination) => {
    sContents = " ";
    iRow = Math.max(0, iIndex - iScrollAt);

    while (iRow < iIndex) {
      sContents += textArray[iRow++] + "<br />";
    }
    destination.innerHTML =
      sContents + textArray[iIndex].substring(0, iTextPos) + "_";

    if (iTextPos++ === iArrLength) {
      iTextPos = 0;
      iIndex++;
      if (iIndex !== textArray.length) {
        iArrLength = textArray[iIndex].length;
        setTimeout(() => typewriter(destination), 500);
      }
    } else {
      setTimeout(() => typewriter(destination), iSpeed);
    }
  };

  return (
    <div className="box">
      <div className="home">
        <h1>Welcome to CodingCalendar</h1>
        <h3>
          Click on a platform in the navigation above to view the upcoming
          contests.
        </h3>
        <div id="typedtext"></div>

        <div className="platform-cards">
          <div className="card">
            <img
              src="data:image/jpeg;base64,
            "
              alt="Codeforces"
            />

            <a
              href="https://codeforces.com"
              target="_blank"
              rel="noopener noreferrer"
            >
              Visit Codeforces
            </a>
          </div>
          <div className="card">
            <img
              src="data:image/png;base64,"
              alt="LeetCode"
            />

            <a
              href="https://leetcode.com"
              target="_blank"
              rel="noopener noreferrer"
            >
              Visit LeetCode
            </a>
          </div>
          <div className="card">
            <img
              src="data:image/jpeg;base64,"
              alt="HackerRank"
            />

            <a
              href="https://hackerrank.com"
              target="_blank"
              rel="noopener noreferrer"
            >
              Visit HackerRank
            </a>
          </div>
          <div className="card">
            <img
              src="data:image/jpeg;base64,"
              alt="CodeChef"
            />

            <a
              href="https://codechef.com"
              target="_blank"
              rel="noopener noreferrer"
            >
              Visit CodeChef
            </a>
          </div>
        </div>
        {/* Footer section */}
        <footer>
          <div className="footer-container">
            <div className="social-links">
              <a
                href="https://www.instagram.com/_the_aromatic_proton_/"
                target="_blank"
                rel="noopener noreferrer"
                aria-label="Instagram"
              >
                <i className="fa fa-instagram"></i>
              </a>
              <a
                href="https://www.linkedin.com/in/nawadeepatreya/"
                target="_blank"
                rel="noopener noreferrer"
                aria-label="LinkedIn"
              >
                <i className="fa fa-linkedin"></i>
              </a>
              <a
                href="https://github.com/Atreya45"
                target="_blank"
                rel="noopener noreferrer"
                aria-label="GitHub"
              >
                <i className="fa fa-github"></i>
              </a>
            </div>
          </div>
          <p className="footer-text">
            &copy; {new Date().getFullYear()} CodingCalendar. All rights
            reserved.
          </p>
        </footer>
      </div>
    </div>
  );
};

export default Home;
